  <script src="https://cdn.jsdelivr.net/npm/@splidejs/splide@4.1.4/dist/js/splide.min.js"></script>
  <script>

  // Get the button that opens the modal
  var nutBtn = document.getElementById("nutrition-facts");
  nutBtn.addEventListener('click', () => {
    window.modalHandler('nutritionModal', true);
  })

  const claimBtn = document.getElementById("claim-1");
  claimBtn.addEventListener('click', () => {
    const ctaArea = document.getElementById("cta-buttons");
    ctaArea.scrollIntoView({behavior: "smooth", block: "start"});
  })

  const sources = document.getElementById("sources");
  const sourcesContent = document.getElementById("sources-content");
  var showSources = false;
  sources.addEventListener('click', () => {
    showSources = !showSources;
    if (showSources) {
      document.activeElement.blur();
    }
  })

  const btn3 = document.getElementById('cta-3');
  const btn6 = document.getElementById('cta-6');
  const btn1 = document.getElementById('cta-1');
  var ctaButtons = [btn3, btn6, btn1];

  // product json object
  var initProduct = btn3.dataset.product;
  var selectedProduct = JSON.parse(initProduct);
  var selectedBtn = btn3;
  ctaButtons.forEach((btn) => {
    btn.addEventListener('click', () => {
      ctaButtons.forEach((bt) => bt.classList.remove('selected'));
      btn.classList.add('selected');
      selectedBtn = btn;
      selectedProduct = !isSubscribed ? JSON.parse(btn.dataset.product) : JSON.parse(btn.dataset.sub);
      updatePrices();
    })
  })

  const gifts = document.getElementById('ebooks');
  const subscribe = document.getElementById('sub');

  var isSubscribed = false;
  subscribe.addEventListener('change', ()=> {
    isSubscribed = subscribe.checked;
    selectedProduct = !isSubscribed ? JSON.parse(selectedBtn.dataset.product) : JSON.parse(selectedBtn.dataset.sub);
    updatePrices();
  })

  // form elements
  const form = document.getElementById('order-form');
  const prodName = document.getElementById('item-name');
  const prodCost = document.getElementById('prod-cost');
  const prodID = document.getElementById('product_id');
  const purchaseBtn = document.getElementById('purchase-btn');

  // display elements
  const retailPrice = document.getElementById('retail-price');
  const salePrice = document.getElementById('sale-price');
  const percentOff = document.getElementById('percent-off');
  const perBottle = document.getElementById('per-bottle');
  const freeShipping = document.getElementById('free-shipping');

  function updatePrices() {
    // update display values
    retailPrice.innerHTML = `${selectedProduct['product_retail']}`;
    salePrice.innerHTML = `${selectedProduct['product_price']}`;
    let perOff = ((selectedProduct['product_retail'] - selectedProduct['product_price']) / selectedProduct['product_retail']) * 100;
    percentOff.innerHTML = Math.round(perOff);
    let perBot = selectedProduct['product_price'] / selectedProduct['product_qty'];
    let perBotAmount = Math.round(perBot * 100) / 100;
    perBottle.innerHTML = perBotAmount.toFixed(2);
    freeShipping.style.display = selectedProduct['product_ship'] > 0 ? 'none' : 'block';
    // update form values
    prodName.value = selectedProduct['product_description'];
    prodCost.value = selectedProduct['product_price'];
    prodID.value = selectedProduct['product_id'];
    form.action = isSubscribed ? '<?= $next_link; ?>' : '<?= $discount_link; ?>';
  }

  purchaseBtn.addEventListener('click', ()=> {
    form.submit();
  })

  document.addEventListener('DOMContentLoaded', function() {
    var splide = new Splide('.splide');
    new Splide('#slider-testimonials').mount();
    new Splide('#slider-images', {
      type: 'loop',
      padding: '3rem'
    }).mount();
    new Splide('#slider-benifits', {
      autoHeight: true,
    }).mount();
    new Splide('#slider-strains', {
      autoHeight: true,
    }).mount();
    // new Splide( '#slider2' ).mount();
    

  });
  </script>
